SELECT *
FROM FAVORITE_TBL
WHERE USER_ID = #{userId} AND REF_NO = #{refNo}

mapper.insert:
INSERT INTO FAVORITE_TBL (USER_ID, REF_NO, DUTY_NAME, DUTY_ADDR, DUTY_TEL1)
SELECT #{userId}, #{refNo}, #{dutyName}, #{dutyAddr}, #{dutyTel1}
FROM DUAL
WHERE NOT EXISTS (
    SELECT 1 FROM FAVORITE_TBL
    WHERE USER_ID = #{userId} AND REF_NO = #{refNo}
)

